mapper.select:
SELECT *
FROM tech_chnl.user

     no = #{data.no}                   
     AND name ~ #{data.name}           
     AND grade = #{data.grade}         
     AND reg_date >= #{data.startDate, jdbcType=TIMESTAMP} 
     AND reg_date <= #{data.endDate  , jdbcType=TIMESTAMP} 

LIMIT #{page.limit} OFFSET #{page.offset}
